import React, {useState,useEffect} from 'react'
import {AiOutlineMenu, AiOutlineClose} from 'react-icons/ai'
import {BsWallet} from 'react-icons/bs'
import { connect } from 'get-starknet'



function App() {
    const[nav, setNav] = useState(true)
    const handleNev = () => {
        setNav(!nav)
    }

    const [provider, setProvider] = useState('')
    const [address, setAddress] = useState('')
    const [name, setName] = useState('')
    const [inputAddress, setInputAddress] = useState('')
    const [retrievedName, setRetrievedName] = useState('')
    const [isConnected, setIsConnected] = useState(false)
    const connectWallet = async() => {
        try{
          // let the user choose a starknet wallet
          const starknet = await connect()
          // connect to the user-chosen wallet
          await starknet?.enable({ starknetVersion: "v4" })
          // set account provider
          setProvider(starknet.account)
          // set user address
          setAddress(starknet.selectedAddress)
          // set connection status
          setIsConnected(true)
        }
        catch(error){
          alert(error.message)
        }
    }

    useEffect(() => {
        connectWallet()
    }, [])


  return (
    <div className='flex justify-between-2 items-center h-24 max-w-[1248px] mx-auto px-4 text-white'>
      <h1 className='w-full text-2xl font-bold text-[#40E0D0]'>LUCKSTARK</h1>
      <ul className='hidden md:flex'>
          <li className='p-4'>Home</li>
          <li className='p-4'>Pool</li>
          <li className='p-4'>About</li>
          {
            isConnected ?
            <button className="font-bold w-[130px] h-[50px] rounded-full bg-[#40E0D0] text-[#ffffff] items-center mx-auto py-3">{address.slice(0, 5)}...{address.slice(60)}</button> :
             <button onClick={() => connectWallet()}className='font-bold w-[130px] h-[50px] rounded-full bg-[#ffffff] text-[#000000] items-center mx-auto py-3'>

               Connect
            </button>
           }
      </ul>

      <div onClick={handleNev} className= 'block md:hidden'>
          {!nav ? <AiOutlineClose size={20}/> : <AiOutlineMenu size={20}/>}

      </div>
      <div className={!nav ? 'fixed left-0 top-3 w-[50%] h-full border-r border-r-gray-900 bg-[#000300] ease-linear duration-500' : 'fixed left-[-100%] '}>
          <h1 className='w-full text-2xl font-bold text-[#40E0D0] m-5'>LUCKSTARK</h1>  
          <ul className='uppercase p-4'>
              <li className='p-4 border-b border-b-gray-400'>Home</li>
              <li className='p-4 border-b border-b-gray-400'>Pool</li>
              <li className='p-4 border-b border-b-gray-400'>Decks</li>
              <li className='p-4 border-b'>About</li>
              {
            isConnected ?
            <button className=" p-4 left-4 selection:font-bold w-[250px] h-[50px] rounded-none bg-[#40E0D0] text-[#ffffff] items-center mx-auto py-3">{address.slice(0, 5)}...{address.slice(60)}</button> :
             <button onClick={() => connectWallet()}className=' top-5 font-bold w-[250px] h-[50px] rounded-none bg-[#ffffff]  text-[#000000] items-center mx-auto py-3'>

               Connect
            </button>
           }

          </ul>
      </div>
    </div>
  )
}

export default Navbar
