import React, {useState,useEffect} from 'react'
import {AiOutlineMenu, AiOutlineClose} from 'react-icons/ai'
import {BsWallet} from 'react-icons/bs'
import { connect } from 'get-starknet'
import {Lİnk} from 'react-scroll'
import Link from 'react-scroll/modules/components/Link'




function Navbar() {
  const[nav, setNav] = useState(true)
  const handleNev = () => {
        setNav(!nav)
  }
  const [provider, setProvider] = useState('')
  const [address, setAddress] = useState('')
  const [name, setName] = useState('')
  const [inputAddress, setInputAddress] = useState('')
  const [retrievedName, setRetrievedName] = useState('')
  const [isConnected, setIsConnected] = useState(false)

  const connectWallet = async() => {

      // let the user choose a starknet wallet
      const starknet = await connect()
      // connect to the user-chosen wallet
      await starknet?.enable({ starknetVersion: "v4" })
      // set account provider
      setProvider(starknet.account)
      // set user address
      setAddress(starknet.selectedAddress)
      // set connection status
      setIsConnected(true)

  }
  const control = () => {
    setIsConnected(!isConnected)
  }


  return (
    <div className=' h-[80px] w-screen z-20 fixed text-white bg-black'>

        <div className='flex items-center font-medium justify-around'>
         <h1 className='w-full text-2xl mr-4 p-10 font-bold text-white'>BilkentDEV</h1>
         <ul className='md:flex hidden uppercase items-center gap-8'>
          <Link to="home" spy={true} smooth={true} offset={50} duration={500} className='py-7 px-3 inline-block'><button className='hover:underline'>Ana Sayfa</button></Link>


         </ul>
        </div>
        <div className='hidden md:flex pr-10'>
          {
            isConnected ?
            <button className="font-bold w-[220px] h-[40px] rounded-full bg-[#40E0D0] text-[#ffffff] full-width mx-auto">{address.slice(0, 7)}...{address.slice(58)}</button> :
             <button onClick={() => connectWallet()}className=' border-none text-flex font-bold w-[220px] h-[40px] rounded-full hover:scale-105 duration-300  bg-white to-pink-500 text-[#000000] full-width mx-auto '>

               Connect to a wallet
            </button>
           }
        </div>

       <div onClick={handleNev} className= 'md:hidden w-8 '>
          {!nav ? <AiOutlineClose size={25}/> : <AiOutlineMenu size={25}/>}

       </div>
        <div className={!nav ? 'fixed left-0 top-3 w-[50%] h-full border-r border-r-gray-900 bg-[#000300] ease-linear duration-500' : 'fixed left-[-100%] '}>
          <h1 className='w-full text-2xl font-bold text-[#ffffff] m-5'>BilkentDEV</h1>  
          <ul className='uppercase p-4'> 
           <Link to="home" spy={true} smooth={true} offset={50} duration={500} className='p-5 font-semibold'><li className='p-4 border-b border-b-gray-400'><button className='hover:underline'>Home</button></li> </Link>
           <Link to="pools" spy={true} smooth={true} offset={50} duration={500} className='p-5 font-semibold'> <li className='p-4 border-b border-b-gray-400'><button className='hover:underline'>Pools</button></li></Link>
              <div className='md:flex mt-2 item-center'>
                {
                isConnected ?
                <button className="font-bold w-[220px] h-[40px]     rounded-full bg-[#40E0D0] text-[#ffffff] full-width     mx-auto">{address.slice(0, 5)}...{address.slice(60)}    </button> :
                 <button onClick={() => connectWallet()}className='     border-none text-flex font-bold w-[220px] h-[40px]     rounded-full hover:scale-105 duration-300      bg-white to-pink-500 text-[#000000] full-width     mx-auto '>

                   Connect to a wallet
                </button>
                }
              </div>       
           </ul> 
       </div>
     </div>

  )
}

export default Navbar
